form-control"   placeholder="Username" required>									</div>									<div class="form-group">										<input type="email" name="email"  class="form-control" placeholder="Email" required >									</div>									<div class="form-group">										<input type="password" name="pass"  id="txtPassword" placeholder="Password" class="form-control" required>										   </div>									<div class="form-group">										<input type="password" name="c_pass"  class="form-control" id="txtConfirmPassword" placeholder="Confirm Password" required>									</div>									<div class="form-group">										<div class="row">											<div class="col-sm-6 col-sm-offset-3">										<input  type="submit" name="submit" value="Register" onclick="return Validate()" class="btn btn-primary">											</div>																	<p>All ready have an account? <a href="login.php">Login</a></p>															<a href="index.php" class="btn btn-primary">Back</a>	 										</div>									</div>								</form>                    </div>                	</div>		</div>		<!-- Custom Js --><script type="text/javascript" src="http://ajax.googleapis.com/ajax/libs/jquery/1.7.1/jquery.min.js">  </script><script type="text/javascript">    function Validate() {        var password = document.getElementById("txtPassword").value;        var confirmPassword = document.getElementById("txtConfirmPassword").value;        if (password != confirmPassword) {            alert("Confirm Passwords do not match.");            return false;        }        return true;		    }</script>	<script>   // only accept number      function isNumber(evt) {    evt = (evt) ? evt : window.event;    var charCode = (evt.which) ? evt.which : evt.keyCode;    if (charCode > 31 && (charCode < 48 || charCode > 57)) {        alert("Please enter only 10 digit number");        return false;    }    return true;}        // only accept char      function checkNum(){var ch = String.fromCharCode(event.keyCode);     var filter = /[a-zA-Z ]/   ;     if(!filter.test(ch)){         alert("Please enter only character");          event.returnValue = false;     }    } function phoneno(){                      $('#phone').keypress(function(e) {                var a = [];                var k = e.which;                for (i = 48; i < 58; i++)                    a.push(i);                if (!(a.indexOf(k)>=0))                    e.preventDefault();            });        }       </script></body></html>